mapper.sql:
WHERE  REGEXP_LIKE(RTRIM(icCode), #{icCode})
       AND enteDate >= #{beginDate} 
       AND #{endDate} >= enteDate
       AND EXISTS (
           SELECT sysid
           FROM fs_org_dept
           WHERE enteName = dept_name
           START WITH sysid = #{stationId}
           CONNECT BY PRIOR sysid = dept_parant_rowid
       )
