mapper.sql:
WHERE EXISTS (
          SELECT sysid
          FROM fs_org_dept
          WHERE exitCode = dept_name
          START WITH sysid = #{stationId}
          CONNECT BY PRIOR sysid = dept_parant_rowid
      ) 
      AND enteDate >= #{beginDate} AND #{endDate} + 1 > enteDate
      AND cardType = #{cardType}
      
          AND REGEXP_LIKE(icCode, #{icCode})
