mapper.sql:
WHERE EXISTS (
           SELECT row_id
           FROM fs_org_dept
           WHERE enteName = dept_name
           START WITH row_id = #{stationId}
           CONNECT BY PRIOR row_id = dept_parant_rowid
       )  
       AND enteDate >= #{beginDate} AND #{endDate} >= enteDate
       
           AND adm_id = '1'
       
       
           AND icCode = #{icCode}
