mapper.select:
          
			SELECT * FROM (
			    SELECT a.*, row_number() OVER (ORDER BY a.exitDate DESC) rn 
			    FROM   enter_car_view a
			    WHERE  REGEXP_LIKE(RTRIM(carExitCode), #{carCode})
			           AND exitDate > #{beginDate} 
			           AND exitDate < #{endDate}
			           AND EXISTS (
					       SELECT sysid
						   FROM fs_org_dept
						   WHERE exitName = dept_name
						   START WITH sysid = #{stationId}
						   CONNECT BY PRIOR sysid = dept_parant_rowid
					   )
			)
			WHERE rn > #{start} and rn <= #{limit}

          
		    SELECT COUNT(recordNo)
		    FROM   enter_car_view
		    WHERE  REGEXP_LIKE(RTRIM(carExitCode), #{carCode})
                   AND exitDate > #{beginDate} 
                   AND exitDate < #{endDate}
                   AND EXISTS (
                       SELECT sysid
                       FROM fs_org_dept
                       WHERE exitName = dept_name
                       START WITH sysid = #{stationId}
                       CONNECT BY PRIOR sysid = dept_parant_rowid
                   )
