mapper.select:
  
    SELECT * FROM (
        SELECT a.*, row_number() OVER (ORDER BY a.log_date DESC) rn 
        FROM   user_scheduler_job_log a
        WHERE  REGEXP_LIKE(lower(a.job_name), #{jobObjName})
               AND to_char(a.log_date, 'yyyy/mm/dd') >= #{beginDate} 
               AND to_char(a.log_date, 'yyyy/mm/dd') <= #{endDate}
    )
    WHERE rn > #{start} and rn <= #{limit}

  
    SELECT COUNT(log_id) 
    FROM   user_scheduler_job_log 
    WHERE  REGEXP_LIKE(lower(job_name), #{jobObjName})
           AND to_char(log_date, 'yyyy/mm/dd') >= #{beginDate} 
           AND to_char(log_date, 'yyyy/mm/dd') <= #{endDate}
