mapper.select:
          
			SELECT * FROM (
			    SELECT a.*, b.username, 
			           row_number() OVER (ORDER BY a.createtime DESC) rn
                FROM   useropration_log a inner join system_user b on a.userid = b.sysid
			    WHERE  REGEXP_LIKE(b.username, #{loginUser})
			           AND to_char(a.createtime, 'yyyy/mm/dd') >= #{beginDate} 
			           AND to_char(a.createtime, 'yyyy/mm/dd') <= #{endDate}
			)
			WHERE rn > #{start} and rn <= #{limit}

          
		    SELECT COUNT(a.sysid)
            FROM   useropration_log a inner join system_user b on a.userid = b.sysid
		    WHERE  REGEXP_LIKE(b.username, #{loginUser})
                   AND to_char(a.createtime, 'yyyy/mm/dd') >= #{beginDate} 
                   AND to_char(a.createtime, 'yyyy/mm/dd') <= #{endDate}
